(14127, 56, '_ua_teens_home_is_list_uk|image|0|0|value', ''),
(14128, 56, '_ua_teens_home_is_list_uk|gallery|0|0|_empty', ''),
(14129, 56, '_ua_teens_home_is_list_uk|title|1|0|value', '<span>UA-Teens</span> – простір з орієнтацією на потреби студента'),
(14130, 56, '_ua_teens_home_is_list_uk|subtitle|1|0|value', 'Індивідуальний навчальний план'),
(14131, 56, '_ua_teens_home_is_list_uk|description|1|0|value', 'Відвідування занять здійснюється за фіксованим самостійно обраним студентом розкладом, але при цьому кожен має власну індивідуальну траєкторію навчання.\r\n\r\nВ залежності від обраних напрямків наше програмне забезпечення будує індивідуальний навчальний план на рік, в якому студент бачить коли і які проєктні лабораторії на нього чекають, та в які місяці необхідно до них якісно підготуватись.'),
(14132, 56, '_ua_teens_home_is_list_uk|media_type|1|0|value', 'image'),
