INSERT INTO `yutedb_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(15560, 57, '_ua_teens_home_is_list_uk|description|0|0|value', 'Наші студенти одночасно вивчають три самостійно обрані напрямки з 8 доступних.\r\nВибір здійснюється після проходження орієнтаційної стадії, де підлітки через практичні вправи знайомляться з усіма програмами і визначаються з тим, що їх цікавить саме зараз.\r\n\r\nВажливо, що змінити свій вибір студент може на будь-якому етапі свого навчання.\r\nУ нас немає «неправильного вибору» і «фатальних помилок». Кожен вибір – це індивідуальний досвід і він найцінніший!'),
(15561, 57, '_ua_teens_home_is_list_uk|media_type|0|0|value', 'image'),
(15562, 57, '_ua_teens_home_is_list_uk|image|0|0|value', ''),
(15563, 57, '_ua_teens_home_is_list_uk|gallery|0|0|_empty', ''),
(15564, 57, '_ua_teens_home_is_list_uk|title|1|0|value', '<span>UA-Teens</span> – простір з орієнтацією на потреби студента'),
(15565, 57, '_ua_teens_home_is_list_uk|subtitle|1|0|value', 'Індивідуальний навчальний план'),
(15566, 57, '_ua_teens_home_is_list_uk|description|1|0|value', 'Відвідування занять здійснюється за фіксованим самостійно обраним студентом розкладом, але при цьому кожен має власну індивідуальну траєкторію навчання.\r\n\r\nВ залежності від обраних напрямків наше програмне забезпечення будує індивідуальний навчальний план на рік, в якому студент бачить коли і які проєктні лабораторії на нього чекають, та в які місяці необхідно до них якісно підготуватись.'),
(15567, 57, '_ua_teens_home_is_list_uk|media_type|1|0|value', 'image'),
(15568, 57, '_ua_teens_home_is_list_uk|image|1|0|value', ''),
(15569, 57, '_ua_teens_home_is_list_uk|gallery|1|0|_empty', ''),
(15570, 57, '_ua_teens_home_social_position_block_title_uk', 'UA-Teens – активна соціальна позиція'),
(15571, 57, '_ua_teens_home_social_position_subtitle_uk', 'Участь студентів у важливих соціальних проєктах'),
(15572, 57, '_ua_teens_home_social_position_text_uk', 'Для нас важливо показати тінейджерам, як практичні навички можуть бути використані для привернення уваги до соціально-важливих тем. Ми віримо, що професійні навички отримують додаткову вагу, якщо їх використовувати з користю та для добрих справ.\r\n\r\nТому спільно з благодійною організацією UAnimals ми запустили проєкт. А також ми є співаорганізаторами та партнерами освітнього проєкту разом з MIT-Ukraine — Leadership &amp; Technology Academy Ukraine by MIT students.'),
