INSERT INTO `yutedb_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(39828, 101, '_ua_teens_home_is_list_uk|description|0|0|value', 'Наші студенти одночасно вивчають три самостійно обрані напрямки з 8 доступних.\r\nВибір здійснюється після проходження орієнтаційної стадії, де підлітки через практичні вправи знайомляться з усіма програмами і визначаються з тим, що їх цікавить саме зараз.\r\n\r\nВажливо, що змінити свій вибір студент може на будь-якому етапі свого навчання.\r\nУ нас немає «неправильного вибору» і «фатальних помилок». Кожен вибір – це індивідуальний досвід і він найцінніший!'),
(39829, 101, '_ua_teens_home_is_list_uk|media_type|0|0|value', 'image'),
(39830, 101, '_ua_teens_home_is_list_uk|image|0|0|value', '83'),
(39831, 101, '_ua_teens_home_is_list_uk|gallery|0|0|_empty', ''),
(39832, 101, '_ua_teens_home_is_list_uk|title|1|0|value', 'простір з орієнтацією на потреби студента'),
(39833, 101, '_ua_teens_home_is_list_uk|subtitle|1|0|value', 'Індивідуальний навчальний план'),
(39834, 101, '_ua_teens_home_is_list_uk|description|1|0|value', 'Відвідування занять здійснюється за фіксованим самостійно обраним студентом розкладом, але при цьому кожен має власну індивідуальну траєкторію навчання.\r\n\r\nВ залежності від обраних напрямків наше програмне забезпечення будує індивідуальний навчальний план на рік, в якому студент бачить коли і які проєктні лабораторії на нього чекають, та в які місяці необхідно до них якісно підготуватись.'),
(39835, 101, '_ua_teens_home_is_list_uk|media_type|1|0|value', 'image'),
(39836, 101, '_ua_teens_home_is_list_uk|image|1|0|value', '82'),
(39837, 101, '_ua_teens_home_is_list_uk|gallery|1|0|_empty', ''),
(39838, 101, '_ua_teens_home_is_list_uk|title|2|0|value', 'практикоорієнтоване навчання'),
(39839, 101, '_ua_teens_home_is_list_uk|subtitle|2|0|value', 'Портфоліо з власними проєктами як результат навчання'),
(39840, 101, '_ua_teens_home_is_list_uk|description|2|0|value', 'Результатом навчання у нас є не сертифікати та дипломи, а портфоліо з власними проєктами за трьома напрямками.\r\n\r\nСаме воно є першою сходинкою на шляху до професійної самореалізації і краще за будь-які слова розкаже і продемонструє, що саме вміє кожен наш студент.'),
